    header('Content-Type: text/html; charset=utf-8');
    $name = $_POST['name'];
    $phone = $_POST['phone'];
    $email = $_POST['email'];
    $subject = $_POST['type'];
    $message = '';

    if(!empty($name)){
        $message = $message.'Имя: '.$name.'<br>';
    }

    if(!empty($phone)){
        $message = $message.' Телефон: '.$phone.'<br>';
    }

    if(!empty($email)){
        $message = $message.' E-mail: '.$email.'<br>';
    }

    switch($subject){
        case "type-4":
        $subject = 'Обучение';
        break;
        case "type-3":
        $subject = 'Макияж';
        break;
        case "type-2":
        $subject = 'Акция';
        break;
        case "type-1":
        $subject = 'Консультация';
        break;
    }

    $message = $message.'<br> Дата отправки сообщения: '.date('d-m-Y');

    require 'class.phpmailer.php';  // подключаем файл с настройками подключения

    $mail = new PHPMailer();
    $mail->CharSet = "utf-8";
    $mail->From = $email;      // от кого
    $mail->FromName = $name;   // от кого
    $mail->AddAddress('klubnica05@mail.ru', 'Анна'); // кому - адрес, Имя
    $mail->IsHTML(true);        // выставляем формат письма HTML
    $mail->Subject = $subject;  // тема письма

    $mail->MsgHTML($message);

        // отправляем наше письмо
        if (!$mail->Send()) die ('Mailer Error: '.$mail->ErrorInfo);
        // ну и просто выводим крикливую надпись, что скрипт отработал
